INSERT INTO `wp_posts` VALUES (10,1,'2025-03-05 20:00:14','2025-03-05 20:00:14','<!-- wp:group {\"style\":{\"spacing\":{\"blockGap\":\"var:preset|spacing|40\",\"padding\":{\"top\":\"var:preset|spacing|50\",\"bottom\":\"var:preset|spacing|50\"},\"margin\":{\"top\":\"0px\",\"bottom\":\"0px\"}},\"dimensions\":{\"minHeight\":\"66.7vh\"}},\"layout\":{\"type\":\"flex\",\"orientation\":\"vertical\",\"verticalAlignment\":\"center\",\"justifyContent\":\"center\",\"flexWrap\":\"wrap\"}} -->\n<div class=\"wp-block-group\" style=\"min-height:66.7vh;margin-top:0px;margin-bottom:0px;padding-top:var(--wp--preset--spacing--50);padding-bottom:var(--wp--preset--spacing--50)\"><!-- wp:image {\"id\":49,\"width\":\"375px\",\"height\":\"auto\",\"sizeSlug\":\"large\",\"linkDestination\":\"none\",\"style\":{\"border\":{\"radius\":\"10px\"}}} -->\n<figure class=\"wp-block-image size-large is-resized has-custom-border\"><img src=\"http://alexanderesser.local/wp-content/uploads/2025/03/heroImageCropped-825x1024.png\" alt=\"Alex Esser\" class=\"wp-image-49\" style=\"border-radius:10px;width:375px;height:auto\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"style\":{\"spacing\":{\"margin\":{\"top\":\"0\",\"bottom\":\"0\",\"left\":\"0\",\"right\":\"0\"}},\"typography\":{\"fontSize\":\"1.4rem\"}}} -->\n<h2 class=\"wp-block-heading has-text-align-center\" style=\"margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;font-size:1.4rem\">I write and edit high-converting content for growth-focused brands.</h2>\n<!-- /wp:heading -->\n\n<!-- wp:group {\"style\":{\"spacing\":{\"margin\":{\"top\":\"0\",\"bottom\":\"0\"}}},\"layout\":{\"type\":\"flex\",\"flexWrap\":\"nowrap\",\"justifyContent\":\"center\"}} -->\n<div class=\"wp-block-group\" style=\"margin-top:0;margin-bottom:0\"><!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button {\"className\":\"is-style-outline\"} -->\n<div class=\"wp-block-button is-style-outline\"><a class=\"wp-block-button__link wp-element-button\" href=\"http://alexanderesser.local/portfolio/\">See my work</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons -->\n\n<!-- wp:buttons {\"layout\":{\"type\":\"flex\",\"justifyContent\":\"center\"}} -->\n<div class=\"wp-block-buttons\"><!-- wp:button -->\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link wp-element-button\" href=\"mailto:alexeresser@gmail.com\">Let\'s talk</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons --></div>\n<!-- /wp:group --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"style\":{\"spacing\":{\"margin\":{\"top\":\"var:preset|spacing|70\",\"bottom\":\"var:preset|spacing|70\"}}},\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\" style=\"margin-top:var(--wp--preset--spacing--70);margin-bottom:var(--wp--preset--spacing--70)\"><!-- wp:heading -->\n<h2 class=\"wp-block-heading\">My Background</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>I’m a content strategist, writer, and editor focused on high-impact marketing.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>I create articles, web content, case studies, ad copy, and newsletters that build trust and drive action—especially for B2B and SaaS brands.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>My interdisciplinary background in psychology, philosophy, and employment relations sharpened my ability to communicate clearly, execute efficiently, and authentically connect with people and ideas.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"style\":{\"spacing\":{\"margin\":{\"top\":\"var:preset|spacing|70\",\"bottom\":\"var:preset|spacing|70\"},\"padding\":{\"top\":\"0\",\"bottom\":\"0\"}}},\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\" style=\"margin-top:var(--wp--preset--spacing--70);margin-bottom:var(--wp--preset--spacing--70);padding-top:0;padding-bottom:0\"><!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Why Work With Me?</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item {\"fontSize\":\"medium\"} -->\n<li class=\"has-medium-font-size\"><strong>Content &amp; Editing</strong> –<strong> </strong>Get clear, compelling messaging that engages your audience and drives results.</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item {\"fontSize\":\"medium\"} -->\n<li class=\"has-medium-font-size\"><strong>Execution &amp; Operations</strong> – Ensure smooth, stress-free content execution with efficient project management.</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item {\"fontSize\":\"medium\"} -->\n<li class=\"has-medium-font-size\"><strong>Creative Problem-Solving</strong> – Leverage psychology, storytelling, and strategy to create content that connects and inspires action.</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item {\"fontSize\":\"medium\"} -->\n<li class=\"has-medium-font-size\"><strong>People &amp; Connection</strong> – Work with someone who blends creativity and logic to craft authentic, engaging content tailored to your brand..</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"style\":{\"spacing\":{\"margin\":{\"top\":\"var:preset|spacing|70\",\"bottom\":\"var:preset|spacing|70\"},\"padding\":{\"top\":\"0\",\"bottom\":\"0\"}}},\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\" style=\"margin-top:var(--wp--preset--spacing--70);margin-bottom:var(--wp--preset--spacing--70);padding-top:0;padding-bottom:0\"><!-- wp:heading -->\n<h2 class=\"wp-block-heading\">What I Do</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item {\"fontSize\":\"medium\"} -->\n<li class=\"has-medium-font-size\"><strong>Content Strategy &amp; Writing</strong> – High-impact content tailored to your audience.</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item {\"fontSize\":\"medium\"} -->\n<li class=\"has-medium-font-size\"><strong>Web Copy</strong> – Conversion-driven messaging that turns visitors into customers.</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item {\"fontSize\":\"medium\"} -->\n<li class=\"has-medium-font-size\"><strong>Case Studies</strong> – Success stories that build trust and authority.</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item {\"fontSize\":\"medium\"} -->\n<li class=\"has-medium-font-size\"><strong>Social Media Content</strong> – Engaging content that amplifies your brand.</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item {\"fontSize\":\"medium\"} -->\n<li class=\"has-medium-font-size\"><strong>Editing &amp; Proofreading</strong> – Precise editing for clarity and impact.</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item {\"fontSize\":\"medium\"} -->\n<li class=\"has-medium-font-size\"><strong>Ad Copy</strong> – Persuasive messaging that drives action.</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"style\":{\"spacing\":{\"margin\":{\"top\":\"var:preset|spacing|70\",\"bottom\":\"var:preset|spacing|70\"}}},\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\" style=\"margin-top:var(--wp--preset--spacing--70);margin-bottom:var(--wp--preset--spacing--70)\"><!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Past and present clients</h2>\n<!-- /wp:heading -->\n\n<!-- wp:group {\"className\":\"clients-grid\",\"style\":{\"spacing\":{\"blockGap\":\"var:preset|spacing|40\"}},\"layout\":{\"type\":\"grid\"}} -->\n<div class=\"wp-block-group clients-grid\"><!-- wp:image {\"lightbox\":{\"enabled\":false},\"id\":21,\"sizeSlug\":\"full\",\"linkDestination\":\"custom\",\"style\":{\"layout\":{\"columnSpan\":1,\"rowSpan\":1}}} -->\n<figure class=\"wp-block-image size-full\"><a href=\"https://stylitics.com/\"><img src=\"http://alexanderesser.local/wp-content/uploads/2025/03/stylitics.png\" alt=\"Stylitics\" class=\"wp-image-21\"/></a></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"lightbox\":{\"enabled\":false},\"id\":20,\"sizeSlug\":\"full\",\"linkDestination\":\"custo