INSERT INTO `wp_posts` VALUES (98,1,'2025-04-06 17:29:54','2025-04-06 17:29:54','<!-- wp:group {\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\"><!-- wp:group {\"style\":{\"spacing\":{\"margin\":{\"top\":\"0\",\"bottom\":\"0\"},\"blockGap\":\"var:preset|spacing|50\"}},\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\" style=\"margin-top:0;margin-bottom:0\"><!-- wp:details {\"style\":{\"typography\":{\"fontSize\":\"2.2rem\"}}} -->\n<details class=\"wp-block-details\" style=\"font-size:2.2rem\"><summary>Articles</summary><!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item {\"style\":{\"typography\":{\"fontSize\":\"1.1rem\"}}} -->\n<li style=\"font-size:1.1rem\"><a href=\"https://stylitics.com/resources/blog/why-revenue-generating-outfitting-solutions-need-transparent-ai/\">Why Revenue-Generating Outfitting Solutions Need Transparent AI</a> <br>— Stylitics, Feb 2025</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item {\"style\":{\"typography\":{\"fontSize\":\"1.1rem\"}}} -->\n<li style=\"font-size:1.1rem\"><a href=\"https://cuebiq.com/measuring-true-impact/?_gl=1*1ugobpe*_ga*MTcwMDEzMjIxMy4xNzQwODc0MDc5*_ga_V2SZ56D5PQ*MTc0MDg3NDA3OC4xLjEuMTc0MDg3NDc4OC41Ni4wLjA\">Measuring True Impact: How Cuebiq Uncovers the Real Effect of Your Campaign</a><br>— Cuebiq, Feb 2025</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item {\"style\":{\"typography\":{\"fontSize\":\"1.1rem\"}}} -->\n<li style=\"font-size:1.1rem\"><a href=\"https://www.hydropoint.com/blog/beyond-the-water-bill-understanding-the-hidden-costs-of-water-blindness/\">Beyond the Water Bill: Understanding the Hidden Costs of Water Blindness</a><br>— HydroPoint, Feb 2025</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item {\"style\":{\"typography\":{\"fontSize\":\"1.1rem\"}}} -->\n<li style=\"font-size:1.1rem\"><a href=\"https://trch.com/the-ruthless-editor-why-detachment-is-key-to-great-content/\">The Ruthless Editor: Why Detachment is Key to Great Content</a><br>— TRCH, Feb 2025</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list --></details>\n<!-- /wp:details -->\n\n<!-- wp:details {\"style\":{\"typography\":{\"fontSize\":\"2.2rem\"}}} -->\n<details class=\"wp-block-details\" style=\"font-size:2.2rem\"><summary>Case Studies</summary><!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item {\"style\":{\"typography\":{\"fontSize\":\"1.1rem\"}}} -->\n<li style=\"font-size:1.1rem\"><a href=\"https://stylitics.com/resources/case-studies/how-rhone-curated-a-seamless-e-commerce-experience-with-stylitics/\">How Rhone Curated a Seamless eCommerce Experience with Stylitics</a><br>— Stylitics, Jan 2025</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item {\"style\":{\"typography\":{\"fontSize\":\"1.1rem\"}}} -->\n<li style=\"font-size:1.1rem\"><a href=\"https://drive.google.com/file/d/1tFjt7QZ088SwF7b4nnCBja0nSzij9mK8/view?usp=drive_link\">The Power of Mobility Data to Predict Fuel Consumption and Supply Chain Activity</a><br>— Spectus, Oct 2022</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list --></details>\n<!-- /wp:details -->\n\n<!-- wp:details {\"style\":{\"typography\":{\"fontSize\":\"2.2rem\"}}} -->\n<details class=\"wp-block-details\" style=\"font-size:2.2rem\"><summary>White Papers</summary><!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item {\"style\":{\"typography\":{\"fontSize\":\"1.1rem\"}}} -->\n<li style=\"font-size:1.1rem\"><a href=\"https://drive.google.com/file/d/15I6Npnf70-bTJTswbKPkzZkIUToq578a/view?usp=drive_link\">Privacy by Design</a><br>— Spectus, Sept 2022</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list --></details>\n<!-- /wp:details -->\n\n<!-- wp:details {\"style\":{\"typography\":{\"fontSize\":\"2.2rem\"}}} -->\n<details class=\"wp-block-details\" style=\"font-size:2.2rem\"><summary>Projects</summary><!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item {\"style\":{\"typography\":{\"fontSize\":\"1.1rem\"}}} -->\n<li style=\"font-size:1.1rem\"><a href=\"https://www.amazon.com/Revenue-Architecture-Jacco-van-Kooij/dp/B0CKZHVMDR/ref=sr_1_1?dib=eyJ2IjoiMSJ9.7oqFEbkizwLGkp8xOy847KXPAZkhgqXZiX9ncl5j_82uRqoL_NpbDC_TqvLQt0nSYCDGBqaSEDiHK13bcmCalk8wxL_dgfJzbNeFzTqJMLg65vLZq28Y9ZGOpCrWjYpxN2SZz6J1b6U7u9Dz92nUkQtNm-3d3JOK6be2IzEb8PH7vblMJ2ufhsak73zgQXBo.fGwgXLn1j6mFoYu_g3lURGWY-WRrL-DYLRcNR-WuhTA&amp;dib_tag=se&amp;qid=1741362024&amp;refinements=p_27%3AJacco+Van+der+Kooij&amp;s=books&amp;sr=1-1\">Revenue Architecture Textbook</a><strong> – </strong>Winning by Design, October 2023<!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item -->\n<li>Managed editorial, production, and distribution processes to bring the Revenue Architecture textbook, ebook, and audiobook to market, generating over $100k in sales in the first 5 months.</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item {\"style\":{\"typography\":{\"fontSize\":\"1.1rem\"}}} -->\n<li style=\"font-size:1.1rem\">This was an enormous undertaking. The textbook is over 500 pages of technical content and graphics. I managed a team of editors, sourced a printer that could produce this behemoth of a textbook, coordinated the distribution of thousands of books to 3PL facilities and Amazon warehouses. To scale distribution, I migrated production to Amazon KDP and sourced and managed contractors to produce ebook and audiobook versions that are available on major platforms like Amazon and Spotify.</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list --></li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item {\"style\":{\"typography\":{\"fontSize\":\"1.1rem\"}}} -->\n<li style=\"font-size:1.1rem\"><strong>KITE Realty Group Award Submission - </strong>Hydropoint, Jan 2025<!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item -->\n<li>2025 E+E Leader Award Winner</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list --></li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item {\"style\":{\"typography\":{\"fontSize\":\"1.1rem\"}}} -->\n<li style=\"font-size:1.1rem\"><a href=\"https://drive.google.com/file/d/0B9-vJPaWppnXN3VQMjVIa1ZQYjBvd2NCRm81X3llY2thS1Zv/view?usp=sharing&amp;resourcekey=0-PO0AlYTGHao-sjNOxZU3tg\">TA Guidebook</a> - University of Waterloo, April 2019</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item {\"style\":{\"typography\":{\"fontSize\":\"1.1rem\"}}} -->\n<li style=\"font-size:1.1rem\"><a href=\"https://drive.google.com/file/d/1a2WtqQn5PyuNwX98KKHFSNuL8P1kZC4I/view?usp=sharing\">Research Paper</a> – Queen’s University, April 2021<!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item {\"style\":{\"typography\":{\"fontSize\":\"1.1rem\"}}} -->\n<li style=\"font-size:1.1rem\">Wrote a research paper about the potential impacts of robotics and AI on the labour market.</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list --></li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item {\"style\":{\"typography\":{\"fontSize\":\"1.1rem\"}}} -->\n<li style=\"font-size:1.1rem\"><a href=\"https://www.facebook.com/watch/?v=10155137241982455\"><strong>Museum Exhibit: All You Can Eat?</strong></a></li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list --></details>\n<!-- /wp:details --></div>\n<!-- /wp:group --></div>\n<!-- /wp:group -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->','Portfolio','','inherit','closed','closed','','61-revision-v1','','','2025-04-06 17:29:54','2025-04-06 17:29:54','',61,'http://alexanderesser.local/?p=98',0,'revision','',0);
INSERT INTO `wp_posts` VALUES (99,1,'2025-04-06 17:31:16','2025-04-06 17:31:16','<!-- wp:group {\"style\":{\"spacing\":{\"blockGap\":\"var:preset|spacing|40\",\"padding\":{\"top\":\"var:preset|spacing|50\",\"bottom\":\"var:preset|spacing|50\"},\"margin\":{\"top\":\"0px\",\"bottom\":\"0px\"}},\"dimensions\":{\"minHeight\":\"66.7vh\"}},\"layout\":{\"type\":\"flex\",\"orientation\":\"vertical\",\"verticalAlignment\":\"center\",\"justifyContent\":\"center\",\"flexWrap\":\"wrap\"}} -->\n<div class=\"wp-block-group\" style=\"min-height:66.7vh;margin-top:0px;margin-bottom:0px;padding-top:var(--wp--preset--spacing--50);padding-bottom:var(--wp--preset--spacing--50)\"><!-- wp:image {\"id\":49,\"width\":\"375px\",\"height\":\"auto\",\"sizeSlug\":\"large\",\"linkDestination\":\"none\",\"style\":{\"border\":{\"radius\":\"10px\"}}} -->\n<figure class=\"wp-block-image size-large is-resized has-custom-border\"><img src=\"http://alexanderesser.local/wp-content/uploads/2025/03/heroImageCropped-825x1024.png\" alt=\"Alex Esser\" class=\"wp-image-49\" style=\"border-radius:10px;width:375px;height:auto\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"style\":{\"spacing\":{\"margin\":{\"top\":\"0\",\"bottom\":\"0\",\"left\":\"0\",\"right\":\"0\"}},\"typography\":{\"fontSize\":\"1.4rem\"}}} -->\n<h2 class=\"wp-block-heading has-text-align-center\" style=\"margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;font-size:1.4rem\">I write and edit high-converting content for growth-focused brands.</h2>\n<!-- /wp:heading -->\n\n<!-- wp:group {\"style\":{\"spacing\":{\"margin\":{\"top\":\"0\",\"bottom\":\"0\"}}},\"layout\":{\"type\":\"flex\",\"flexWrap\":\"nowrap\",\"justifyContent\":\"center\"}} -->\n<div class=\"wp-block-group\" style=\"margin-top:0;margin-bottom:0\"><!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button {\"className\":\"is-style-outline\"} -->\n<div class=\"wp-block-button is-style-outline\"><a class=\"wp-block-button__link wp-element-button\" href=\"http://alexanderesser.local/portfolio/\">See my work</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons -->\n\n<!-- wp:buttons {\"layout\":{\"type\":\"flex\",\"justifyContent\":\"center\"}} -->\n<div class=\"wp-block-buttons\"><!-- wp:button -->\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link wp-element-button\">Let\'s talk</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons --></div>\n<!-- /wp:group --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"style\":{\"spacing\":{\"margin\":{\"top\":\"var:preset|spacing|70\",\"bottom\":\"var:preset|spacing|70\"}}},\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\" style=\"margin-top:var(--wp--preset--spacing--70);margin-bottom:var(--wp--preset--spacing--70)\"><!-- wp:heading -->\n<h2 class=\"wp-block-heading\">My Background</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>I’m a content strategist, writer, and editor focused on high-impact marketing.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>I create articles, web content, case studies, ad copy, and newsletters that build trust and drive action—especially for B2B and SaaS brands.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>My interdisciplinary background in psychology, philosophy, and employment relations sharpened my ability to communicate clearly, execute efficiently, and authentically connect with people and ideas.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"style\":{\"spacing\":{\"margin\":{\"top\":\"var:preset|spacing|70\",\"bottom\":\"var:preset|spacing|70\"},\"padding\":{\"top\":\"0\",\"bottom\":\"0\"}}},\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\" style=\"margin-top:var(--wp--preset--spacing--70);margin-bottom:var(--wp--preset--spacing--70);padding-top:0;padding-bottom:0\"><!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Why Work With Me?</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul class=\"wp-blo